INSERT INTO questions (id, domain, component, text, tag) VALUES
('V_ST_01', 'Vision', 'Strategic Thinking', 'ბუნებრივად ვაკავშირებ ცალკეულ იდეებს, რათა აღვიქვა უფრო დიდი სურათი.', 'Strategic Thinking'),
('V_ST_02', 'Vision', 'Strategic Thinking', 'მომავალს წინასწარ ვგეგმავ, გადაწყვეტილებების გრძელვადიანი შედეგების გათვალისწინებით.', 'Strategic Thinking'),
('V_ST_03', 'Vision', 'Strategic Thinking', 'როდესაც ახალი გამოწვევის წინაშე ვარ, უფრო მეტად ვკონცენტრირდები გრძელვადიანი ტენდენციებისა და უხილავი კავშირების პოვნაზე, ვიდრე გამოწვევების გადასალახად ადამიანებისა და კულტურის ერთი იდეის ირგვლივ გაერთიანებაზე.', 'Strategic Thinking vs Trust-Building'),
('V_VA_01', 'Vision', 'Vision Articulation', 'შემიძლია კომპლექსური იდეები ისე ავხსნა, რომ სხვები დავაინტერესო და შთავაგონო.', 'Vision Articulation'),
('V_VA_02', 'Vision', 'Vision Articulation', 'ადამიანები ხშირად მკაფიოდ აღიქვამენ ჩემს ხედვებს მომავლის შესახებ, დამატებითი ახსნისა და განმარტებების გარეშე.', 'Vision Articulation'),
('V_VA_03', 'Vision', 'Vision Articulation', 'ცვლილების მართვისას მირჩევნია, სხვები დავაინტერესო და შთავაგონო მოვლენების უფრო დიდი სურათით, ვიდრე განვსაზღვრო ამ ცვლილების შესრულების გეგმა ნაბიჯ-ნაბიჯ და დეტალური სიზუსტით.', 'Vision Articulation vs Goal Orientation'),
('V_CI_01', 'Vision', 'Creativity & Innovation', 'არსებულ იდეებსა და მეთოდებს ხშირად ვაერთიანებ ახალი, უფრო ეფექტური გადაწყვეტების შესაქმნელად.', 'Creativity & Innovation'),
('V_CI_02', 'Vision', 'Creativity & Innovation', 'გამოწვევებთან გასამკლავებლად ყოველთვის ვეძებ არატრადიციულ გზებს.', 'Creativity & Innovation'),
('V_CI_03', 'Vision', 'Creativity & Innovation', 'პრობლემის გადაჭრისას, უფრო მეტად მიდრეკილი ვარ თამამი, ახალი მეთოდის გამოყენებისკენ, ვიდრე ტრადიციული სამუშაო პროცესებისა და მიდგომების ოპტიმიზაციისკენ.', 'Creativity & Innovation vs Operational Discipline'),
('V_PDM_01', 'Vision', 'Purpose-Driven Mindset', 'ჩემი გადაწყვეტილებები ყოველთვის შეესაბამება მისიას და ძირითად ღირებულებებს.', 'Purpose-Driven Mindset'),
('V_PDM_02', 'Vision', 'Purpose-Driven Mindset', 'მიუხედავად სირთულეებისა თუ გამოწვევებისა, მუდამ ერთგული ვრჩები იმ მიზნებისა, რომლებიც ჩემს მისიას უფრო ემსახურება.', 'Purpose-Driven Mindset'),
('V_PDM_03', 'Vision', 'Purpose-Driven Mindset', 'უპირატესობას ვანიჭებ იმ გადაწყვეტილებებს, რომლებიც უფრო მეტად ემსახურება გრძელვადიან მიზანს, ვიდრე გუნდური ნდობისა და ურთიერთობების გაძლიერებას.', 'Purpose-Driven Mindset vs Empathy'),
('V_SYT_01', 'Vision', 'Systemic Thinking', 'ყოველთვის ვამახვილებ ყურადღებას, თუ როგორ მოქმედებს ერთ ბიზნეს დომეინში მიღებული გადაწყვეტილებები მთლიან ორგანიზაციაზე.', 'Systemic Thinking'),
('V_SYT_02', 'Vision', 'Systemic Thinking', 'მოცემულობას აღვიქვამ როგორც ურთიერთდაკავშირებული სისტემის ელემენტს და არა როგორც იზოლირებულ შემთხვევებს განსხვავებული მახასიათებლებით.', 'Systemic Thinking'),
('V_SYT_03', 'Vision', 'Systemic Thinking', 'მე უფრო მეტ მნიშვნელობას ვანიჭებ იმას, თუ როგორ ფუნქციონირებს სისტემა მთლიანობაში, ვიდრე ინდივიდუალური მიზნების რაც შეიძლება სწრაფად მიღწევას.', 'Systemic Thinking vs Outcome Orientation'),
('V_AT_01', 'Vision', 'Adaptive Thinking', 'როცა მოულოდნელი ცვლილებები ხდება, სწრაფად ვცვლი გეგმებს მოცემულობის შესაბამისად.', 'Adaptive Thinking'),
('V_AT_02', 'Vision', 'Adaptive Thinking', 'ეფექტური ვრჩები მაშინაც კი, როცა გარემოებები მოულოდნელად იცვლება.', 'Adaptive Thinking'),
('V_AT_03', 'Vision', 'Adaptive Thinking', 'გაურკვევლობისას უფრო ვკონცენტრირდები სტრატეგიის შეცვლასა და მოქნილობაზე, ვიდრე გუნდის ემოციურ სტაბილურობასა და ერთიანობაზე.', 'Adaptive Thinking vs Emotional Intelligence'),
('P_E_01', 'People', 'Empathy', 'ვგრძნობ სხვების განწყობას ისე, რომ მათ ამის თქმა არ სჭირდებათ.', 'Empathy'),
('P_E_02', 'People', 'Empathy', 'ყოველთვის ვცდილობ ადამიანებს ისე ვუპასუხო, რომ გავითვალსიწინო მათი ემოციები.', 'Empathy'),
('P_E_03', 'People', 'Empathy', 'როდესაც გუნდის წევრს რთული პერიოდი აქვს, უფრო მეტად ვცდილობ გავიგო მისი განცდები და ემოციური მდგომარეობა, ვიდრე დავეხმარო ამოცანების პრიორიტეტიზაციასა და საქმის დასრულებაში.', 'Empathy vs Prioritization'),
('P_EI_01', 'People', 'Emotional Intelligence', 'სტრესის დროსაც კი ყოველთვის ვმართავ ჩემს ემოციებს.', 'Emotional Intelligence'),
('P_EI_02', 'People', 'Emotional Intelligence', 'შემიძლია პოზიტიური გავლენა მოვახდინო ჩემს ირგვლივ მყოფი ადამიანების განწყობაზე.', 'Emotional Intelligence'),
('P_EI_03', 'People', 'Emotional Intelligence', 'დაძაბულ მომენტებში გუნდში ემოციური სტაბილურობის შექმნა ჩემთვის პრიორიტეტია და მხოლოდ ამის შემდეგ ვკონცენტრირდები საერთო სტრატეგიასა და გრძელვადიან მიზნებზე.', 'Emotional Intelligence vs Strategic Thinking'),
('P_IM_01', 'People', 'Intrinsic Motivation', 'ყოველთვის ვრჩები დასახული მიზნების ერთგული, მიუხედავად იმისა, არსებობს თუ არა გარე მამოტივირებელი ფაქტორები.', 'Intrinsic Motivation'),
('P_IM_02', 'People', 'Intrinsic Motivation', 'თავს ყოველთვის ვუბიძგებ მაღალი სტანდარტებით მუშაობისკენ.', 'Intrinsic Motivation'),
('P_IM_03', 'People', 'Intrinsic Motivation', 'ჩემთვის უფრო მნიშვნელოვანია ვაკეთო ის საქმე, რომელიც მნიშვნელოვანი მგონია, ვიდრე შევასრულო ყველა მიზანი და მივიღო გაზომვადი შედეგები.', 'Intrinsic Motivation vs Outcome Orientation'),
('P_EO_01', 'People', 'Empowering Others', 'სხვებს (გუნდის წევრებს) ვუბიძგებ მიიღონ საკუთარი გადაწყვეტილებები და აიღონ ამ გადაწყვეტილებებზე პასუხისმგებლობა.', 'Empowering Others'),
('P_EO_02', 'People', 'Empowering Others', 'ვუზრუნველყოფ რესურსებსა და გარემოს, რომლებიც ადამიანებს საკუთარი უნარების განვითარებაში ეხმარება.', 'Empowering Others'),
('P_EO_03', 'People', 'Empowering Others', 'მე მირჩევნია, მხარი დავუჭირო ადამიანებს, აიღონ პასუხისმგებლობა საკუთარ თავზე, ვიდრე შევქმნა/დავხვეწო სისტემა, რომელიც მათ უბიძგებს ეფექტიანობისკენ.', 'Empowering Others vs Operational Discipline'),
('P_NB_01', 'People', 'Network Building', 'ვინარჩუნებ ურთიერთობებს, რომლებიც უშუალო საჭიროებებსა და მხოლოდ საქმიან კავშირებს სცდება.', 'Network Building'),
('P_NB_02', 'People', 'Network Building', 'მსიამოვნებს, როდესაც ერთმანეთთან ვაკავშირებ ადამიანებს, რომლებსაც ერთმანეთის დახმარება შეუძლიათ.', 'Network Building'),
('P_NB_03', 'People', 'Network Building', 'მირჩევნია ვიმუშაო ადამიანებთან, ვინც ახალ ხედვებს აზიარებს და ჩემს მიდგომებს აჩელენჯებს, ვიდრე მათთან, ვისთანაც უკვე კომფორტულად ვარ შეთამაშებული.', 'Network Building vs Creativity & Innovation'),
('P_TB_01', 'People', 'Trust-Building', 'მიმაჩნია, რომ ჩემთან გულწრფელად და გულახდილად საუბრისას ადამიანები თავს დაცულად გრძნობენ.', 'Trust-Building'),
('P_TB_02', 'People', 'Trust-Building', 'ყოველთვის ვასრულებ დანაპირებს.', 'Trust-Building'),
('P_TB_03', 'People', 'Trust-Building', 'უფრო მეტად ვამახვილებ ყურადღებას ურთიერთნდობის ჩამოყალიბებაზე დროთა განმავლობაში, ვიდრე ამბიციური მიზნების დასახვაზე სწრაფი შედეგის მისაღებად.', 'Trust-Building vs Goal Orientation'),
('E_GO_01', 'Execution', 'Goal Orientation', 'ყოველთვის ვსახავ კონკრეტულ, გაზომვად ამოცანებს ჩემთვის და ჩემი გუნდისთვის.', 'Goal Orientation'),
('E_GO_02', 'Execution', 'Goal Orientation', 'მუდამ მტკიცედ მივიწევ გრძელვადიანი მიზნებისკენ ყოველდღიურად, ყოველკვირეულად და ყოველთვიურად.', 'Goal Orientation'),
('E_GO_03', 'Execution', 'Goal Orientation', 'მირჩევნია განვსაზღვრო მკაფიო, გაზომვადი მიზნები ცალკეული გუნდის წევრისთვის, ვიდრე დავრწმუნდე, რომ მომავლის ხედვა არის მკაფიო და ინსპირაციული ყველასთვის.', 'Goal Orientation vs Vision Articulation'),
('E_D_01', 'Execution', 'Decisiveness', 'მნიშვნელოვან გადაწყვეტილებებს ზედმეტი ფიქრის გარეშე, დროულად ვიღებ.', 'Decisiveness'),
('E_D_02', 'Execution', 'Decisiveness', 'გადაწყვეტილების მიღების შემდეგ არასდროს ვნანობ ჩემს არჩევანს.', 'Decisiveness'),
('E_D_03', 'Execution', 'Decisiveness', 'გადაწყვეტილების მიღებისას, მე უფრო მიდრეკილი ვარ სწრაფად ვიმოქმედო ხელთ არსებული ინფორმაციით, ვიდრე მეტი დრო დავუთმო იმის განხილვას, თუ როგორ გავლენას მოახდენს ეს ადამიანებსა და ურთიერთობებზე.', 'Decisiveness vs Empathy'),
('E_A_01', 'Execution', 'Accountability', 'სრულ პასუხისმგებლობას ვიღებ ჩემი გადაწყვეტილებების შედეგებზე, კარგზე თუ ცუდზე.', 'Accountability'),
('E_A_02', 'Execution', 'Accountability', 'სხვებისგან მოველი, რომ ზედმიწევნით შეასრულებენ ნაკისრ ვალდებულებებს.', 'Accountability'),
('E_A_03', 'Execution', 'Accountability', 'ცვლილებების დროს მიმაჩნია, რომ უფრო მნიშვნელოვანია შეთანხმებული შედეგების მიღწევაზე პასუხისმგებლობის აღება და შენარჩუნება, ვიდრე გეგმის ახალ გარემოებებთან შესაბამისობაში მოყვანა.', 'Accountability vs Adaptive Thinking'),
('E_P_01', 'Execution', 'Prioritization', 'პირველ რიგში, ყველაზე მნიშვნელოვან ამოცანებს განვსაზღვრავ და მათზე ვამახვილებ ყურადღებას.', 'Prioritization'),
('E_P_02', 'Execution', 'Prioritization', 'პრიორიტეტებს ვცვლი გარემოების ცვლილების შესაბამისად.', 'Prioritization'),
('E_P_03', 'Execution', 'Prioritization', 'მირჩევნია ვფოკუსირდე იმაზე, რაც ახლა ყველაზე გადაუდებელია, ვიდრე ჩემი დროისა და ენერგიის ინვესტიცია ჩავდო იმაში, რაც გრძელვადიან მიზნებს ემსახურება.', 'Prioritization vs Purpose-Driven Mindset'),
('E_OD_01', 'Execution', 'Operational Discipline', 'მჯერა, რომ ხარისხის უზრუნველსაყოფად აუცილებელია ყოველთვის მივყვე სტრუქტურირებულ ბიზნეს პროცესს.', 'Operational Discipline'),
('E_OD_02', 'Execution', 'Operational Discipline', 'უფრო კომფორტულად ვგრძნობ თავს, როდესაც მივყვები წინასწარ განსაზღვრულ პროცესებსა და სისტემებს, რომლებიც სამუშაოს სიცხადესა და პროგნოზირებადობას მატებს.', 'Operational Discipline'),
('E_OD_03', 'Execution', 'Operational Discipline', 'თანმიმდევრულობისთვის მე უფრო მეტად დადგენილ პროცედურებს ვეყრდნობი და მირჩევნია, რომ გუნდმა დაიცვას ისინი, ვიდრე გუნდის წევრებს ჰქონდეთ თავისუფლება სხვადასხვა სიტუაციაში საკუთარი მიდგომებისა და მეთოდების გამოყენებისას.', 'Operational Discipline vs Empowering Others'),
('E_OO_01', 'Execution', 'Outcome Orientation', 'მჯერა, რომ წარმატება მიღწეული შედეგებით განისაზღვრება და არა - გაწეული ძალისხმევით.', 'Outcome Orientation'),
('E_OO_02', 'Execution', 'Outcome Orientation', 'სამუშაოს პროგრესს ვაკონტროლებ შესრულების მკაფიო ინდიკატორების გამოყენებით.', 'Outcome Orientation'),
('E_OO_03', 'Execution', 'Outcome Orientation', 'ყურადღებას უფრო მეტად ვამახვილებ კონკრეტული შედეგის მიღწევაზე, ვიდრე ხვალინდელი ინოვაციური შესაძლებლობების შექმნაზე.', 'Outcome Orientation vs Creativity & Innovation'),
('EI_EXT_01', 'E/I', 'Extraversion', 'ბევრ ადამიანთან ურთიერთობის შემდეგ თავს ენერგიულად ვგრძნობ.', 'Extraversion'),
('EI_EXT_02', 'E/I', 'Extraversion', 'უფრო ენერგიულად ვგრძნობ თავს წყნარად ფიქრში გატარებული დროის შემდეგ.', 'Extraversion'),
('EI_EXT_03', 'E/I', 'Extraversion', 'გუნდურ გარემოში მირჩევნია ჩემი აზრები სიტყვიერად იქვე გამოვხატო საუბრით, ვიდრე მარტო დავფიქრდე და შემდეგ გავაზიარო, რას ვფიქრობ.', 'Extraversion'),
('EI_INT_01', 'E/I', 'Introversion', 'უფრო ენერგიულად ვგრძნობ თავს წყნარად ფიქრში გატარებული დროის შემდეგ.', 'Introversion'),
('EI_INT_02', 'E/I', 'Introversion', 'დატვირთული დღეების შემდეგ მარტო ყოფნა მჭირდება ენერგიის აღსადგენად', 'Introversion'),
('EI_INT_03', 'E/I', 'Introversion', 'მირჩევნია, რომ იდეებზე ჩემით დავფიქრდე; შინაგანად გავიაზრო, სანამ გავაზიარებ, რას ვფიქრობ, ვიდრე ღიად ჩავერთო დისკუსიაში და ჩემი ყველა აზრი ხმამაღლა გამოვხატო.', 'Introversion');
INSERT INTO profiles (code, title, style_name, dominant_orientation, supporting_orientation, blind_spot, description, strengths, development_areas, pitfalls) VALUES
('VPEE', 'ინსპირაციული ვიზიონერი', 'ინსპირაციული ვიზიონერი', 'Vision (ხედვა)', 'People (ადამიანები)', 'Execution (აღსრულება)', 'ინსპირაციული ვიზიონერი არის ლიდერი ქარიზმატული ენერგიით, რომელიც სხვებს იზიდავს ძლიერი ხედვითა და ეფექტური კომუნიკაციით. მისთვის ბუნებრივია, საჯაროდ გააზიაროს იდეები, შთააგონოს გუნდი და ადამიანებს აგრძნობინოს, რომ ისინი არიან უფრო დიდი მიზნის ნაწილი. იგი ქმნის მოტივაციისა და ერთიანობის ატმოსფეროს, თუმცა ხანდახან ნაკლებ ყურადღებას უთმობს აღსრულების პროცესებსა და მხარდამჭერ სტრუქტურებს.', ARRAY['მომავლის მკაფიო ხედვის ჩამოყალიბებისა და სხვებისთვის მისი გაზიარების უნარი.','ბუნებრივი ქარიზმა და ენერგია, რომელიც გუნდს აძლევს მოტივაციას.','სისტემური აზროვნების უნარი, რომელიც აერთიანებს მრავალფეროვან ფაქტორებს.','ემპათია და ადამიანებზე ფოკუსირება, რაც ქმნის ნდობას და თანამშრომლობის კულტურას.','ძლიერი კომუნიკაციისა და საჯარო გამოსვლების უნარი, რაც ხელს უწყობს გუნდის წევრების გაერთიანებას სერთო იდეის გარშემო.','სიახლეების ინიცირება და შემოქმედებითი მიდგომები.'], ARRAY['აღსრულების უნარის გაძლიერება – იდეების პრაქტიკაში გადატანა და შედეგებზე მეტი ფოკუსი.','პრიორიტეტების დასახვა – ალტერნატივებს შორის საუკეთესოს იდეის შერჩევა, მისი მკაფიოდ სტრუქტურირება და გეგმად ქცევა.','ოპერაციული თანმიმდევრულობა – ენერგიის შენარჩუნება შედეგების მონიტორინგისა და სისტემების გაძლიერების გზით.'], ARRAY['შეიძლება ოპერაციულ დეტალებს სათანადო მნიშვნელობას არ ანიჭებდეს.','შესაძლოა, გაუჭირდეს იდეების პრაქტიკაში გადატანა.','გუნდი შესაძლოა შთაგონებული იყოს ასეთი ლიდერით, მაგრამ გარკვეული მიმართულების გარეშე დარჩეს.','შესაძლოა, გადაჭარბებულად მოახდინოს ამოცანების დელეგირება და შედეგად დაკარგოს კონტროლი.','პრიორიტეტების მუდმივმა ცვლილებებმა შეიძლება გუნდის გადაღლა და მოტივაციის შემცირება გამოიწვიოს.']),
('VPEI', 'რეფლექსიური კრეატორი', 'რეფლექსიური კრეატორი', 'Vision (ხედვა)', 'People (ადამიანები)', 'Execution (აღსრულება)', 'რეფლექსიური კრეატორი არის ლიდერი, რომელიც ქმნის დამაჯერებელ ხედვას და შთააგონებს მის ახლო, სანდო თანამშრომლებს. მისი სტილი რეფლექსიური და ანალიტიკურია; ურჩევნია ჯერ იფიქროს, შემდეგ კი ისაუბროს. ამ სტილის ლიდერები არ არიან ყველაზე შესამჩნევი ადამიანები ოთახში, მაგრამ მათი გავლენა ძლიერია. რეფლექსიური კრეატორები ამჯობინებენ ინდივიდუალურ ან მცირე ჯგუფურ საუბრებს შეთანხმების მისაღწევად და გამოირჩევიან გააზრებული, მიზანმიმართული კომუნიკაციის უნარით.', ARRAY['ღრმა და გააზრებული ხედვის ჩამოყალიბება.','სისტემური აზროვნება და გრძელვადიანი პერსპექტივის დანახვის უნარი.','ემპათიური დამოკიდებულება, რომელიც ხელს უწყობს ნდობის შექმნას.','გუნდში ნელა, მაგრამ მყარად მოტივაციისა და ერთიანობის ჩამოყალიბება.','ინოვაციური იდეების ჩამოყალიბება საფუძვლიანი ანალიზის შედეგად.','მაღალი პასუხისმგებლობა და მისიის ერთგულება.'], ARRAY['მოქმედების აჩქარება – იდეების რეალიზაცია დროულად, სანამ ისინი არ დაკარგავენ აქტუალურობას.','კომუნიკაციის გაძლიერება – საკუთარი ხედვის უფრო აქტიურად გაზიარება გუნდთან.','პრიორიტეტიზაცია – იდეების გადატანა სტრუქტურირებულ გეგმასა და ქმედებებში.'], ARRAY['შეიძლება გადაჭარბებულად კონცენტრირდეს სრულყოფილებაზე და დაკარგოს დრო.','შესაძლოა უჭირდეს იდეების გაზიარება ფართო აუდიტორიისთვის.','პრიორიტეტების მუდმივმა ცვლილებებმა შეიძლება გუნდის გადაღლა და მოტივაციის შემცირება გამოიწვიოს.','შესაძლებელია გადადოს გადაწყვეტილებების მიღება, რათა მეტი ფიქრი მოასწროს.']),
('VEPE', 'სტრატეგიული აღმასრულებელი', 'სტრატეგიული აღმასრულებელი', 'Vision (ხედვა)', 'Execution (აღსრულება)', 'People (ადამიანები)', 'სტრატეგიული აღმასრულებელი ქარიზმატული ლიდერია, რომელიც დიდ სურათს ხედავს და მას კონკრეტულ ქმედებებად აქცევს. მისთვის ბუნებრივია, ხმამაღლა გააზიაროს გეგმები, მიიპყროს ყურადღება და გუნდს მისცეს მკაფიო მიმართულება. იგი ხშირად არის ცვლილებების წინა პლანზე და საკუთარ მაგალითზე აჩვენებს, როგორ შეიძლება სტრატეგია გადაიქცეს ქმედებად, შემდეგ კი მივიღოთ შედეგი, თუმცა, ხანდახან ნაკლებად აქცევს ყურადღებას გუნდის ემოციურ ფონს და ურთიერთობების სიღრმეს.', ARRAY['ხედვის სტრატეგიულ ქმედებად გარდაქმნის უნარი.','მკაფიო პრიორიტეტები და შედეგზე ორიენტირებული მიდგომა.','ენერგია და დინამიკურობა, რომელიც გუნდს აძლევს მოტივაციას.','სწრაფი და თამამი გადაწყვეტილებების მიღება.','სტრატეგიული კომუნიკაცია და გუნდის მობილიზება.'], ARRAY['ემპათიის გაძლიერება – მეტი დროისა და ყურადღების დათმობა ადამიანების მოსასმენად.','კულტურულ ღირებულებებზე მუშაობა – სტრატეგია მხოლოდ მაშინ არის ეფექტური, როცა ის ხალხის მხარდაჭერასაც მოიპოვებს.','გუნდური პროცესების გათვალისწინება – შედეგზე ფოკუსის პარალელურად, საჭირო ხდება ურთიერთობების შენარჩუნება.'], ARRAY['შეიძლება ზედმეტად კონცენტრირდეს შედეგებზე და ნაკლებად მოუსმინოს სხვების ემოციებს.','შესაძლოა მიიღოს სწრაფი გადაწყვეტილებები საკმარისი კონსულტაციის გარეშე.']),
('VEPI', 'ანალიტიკური სტრატეგი', 'ანალიტიკური სტრატეგი', 'Vision (ხედვა)', 'Execution (აღსრულება)', 'People (ადამიანები)', 'ანალიტიკური სტრატეგი გამოირჩევა სიღრმისეულად გააზრებული გადაწყვეტილებებით და გრძელვადიანი გეგმებით. სიძლიერე მათ ხედვასა და აღსრულებას შორის მჭიდრო კავშირს მოიაზრებს. ანალიტიკური ბუნება მათ ეხმარება პოტენციური რისკების ამოცნობასა და საიმედო სტრუქტურების შექმნაში. Ისინი ექსპერტები არიან გეგმის შემუშავებაში, რაც ხელს აძლევს სტრატეგიული ხედვების განხორციელებას. ასეთი ლიდერი ფრთხილად აანალიზებს მონაცემებსა და გარემოს, შემდეგ კი ქმნის სამოქმედო სტრატეგიას, თუმცა ხანდახან ზედმეტი ანალიზი ან სიფრთხილე შეიძლება აყოვნებდეს პროცესს.', ARRAY['მონაცემებსა და ფაქტებზე დაფუძნებული სტრატეგიები.','სისტემური აზროვნების უნარი და გრძელვადიანი სტაბილურობა.','მკაფიო პრიორიტეტების დასახვის უნარი.','ჩუმი, მაგრამ ძლიერი გავლენა გუნდის წევრებზე.'], ARRAY['სისწრაფე გადაწყვეტილებების მიღებისას – დროული რეაგირება, როცა სიტუაცია ამას მოითხოვს.','იდეების გაზიარება – საკუთარ ხედვასა და იდეებზე გუნდთან უფრო აქტიური კომუნიკაცია.','ადამიანურ ურთიერთობებზე მეტი კონცენტრაცია - კულტურული და ემოციური ფაქტორების გათვალისწინება.'], ARRAY['შეიძლება ზედმეტად ფრთხილი იყოს და არ გამოიყენოს შესაძლებლობები.','შესაძლოა გადაჭარბებულად ფოკუსირდეს დეტალებზე და არ იმოქმედოს.','ნაკლები კომუნიკაცია შეიძლება აღქმული იყოს როგორც ჩაკეტილობა.']),
('PVEE', 'ურთიერთობების არქიტექტორი', 'ურთიერთობების არქიტექტორი', 'People (ადამიანები)', 'Vision (ხედვა)', 'Execution (აღსრულება)', 'ურთიერთობების არქიტექტორი ქარიზმატული ენერგიით აერთიანებს ადამიანებს და ავითარებს თანამშრომლობის კულტურას. მისთვის ბუნებრივია იყოს ჯგუფის ცენტრში, გაამხნევოს სხვები და გააერთიანოს ადამიანები საერთო იდეის გარშემო, თუმცა ხანდახან ნაკლებ ყურადღებას აქცევს პროცესების სტრუქტურას და სამოქმედო მექანიზმებს.', ARRAY['ფართო ქსელების შექმნისა და ადამიანების დაკავშირების უნარი.','ეფექტური კომუნიკაცია.','ნდობისა და ერთიანობის ჩამოყალიბება.','თანამშრომლობის ხელშეწყობა და პოზიტიური კულტურის ჩამოყალიბება.'], ARRAY['პროცესების გაძლიერება – ურთიერთობების პარალელურად აუცილებელია სტრუქტურა და შედეგების კონტროლი.','პრიორიტეტიზაცია – მრავალფეროვან ინიციატივებს სჭირდება მკაფიო ფოკუსი.','სტრატეგიულ ხედვასთან ინტეგრაცია – ურთიერთობების ქსელები უნდა ემსახურებოდეს გრძელვადიან მიზნებს.'], ARRAY['შესაძლოა მეტად ენდოს ადამიანურ ურთიერთობებს და ნაკლებად გააკონტროლოს შედეგები.','შეიძლება კონფლიქტების თავიდან არიდების მიზნით დაკარგოს მკაფიო პოზიცია.','შესაძლოა გუნდი იყოს მოტივირებული, მაგრამ ნაკლებად ორგანიზებული.']),
('PVEI', 'ემპათიური მენტორი', 'ემპათიური მენტორი', 'People (ადამიანები)', 'Vision (ხედვა)', 'Execution (აღსრულება)', 'ემპათიური მენტორი ქმნის სიღრმისეულ, ნდობაზე დამყარებულ ურთიერთობებს და ფოკუსირდება ადამიანების განვითარებაზე. იგი ნაკლებად ხმაურიანი, მაგრამ მეტად სანდო და დამაჯერებელი ლიდერია. უყვარს სხვების მოსმენა, მხარდაჭერა და მათთვის ინდივიდუალური გზის გაკვალვა. იგი ემპათიურია, თუმცა ხანდახან შეიძლება ნაკლებად იყოს ორიენტირებული სწრაფ რეაგირებასა და შედეგებზე.', ARRAY['ძლიერი ემპათიის უნარი','ნდობისა და სანდოობის შექმნის უნარი.','მენტორობა და სხვების განვითარებაზე ორიენტაცია.','სტაბილური და თანმიმდევრული მოქმედება.','მშვიდი და დამაჯერებელი ხასიათი.'], ARRAY['სწრაფი რეაგირება – დროული მოქმედება მაშინაც, როცა გარემო სწრაფად იცვლება.','კომუნიკაციის გაძლიერება – საკუთარ ხედვებზე აქტიური კომუნიკაცია გუნდთან.','შედეგებზე კონცენტრაცია – ადამიანური მხარდაჭერის პარალელურად, მეტი ყურადღება შედეგებსა და პროცესებზე.'], ARRAY['შეიძლება გადაჭარბებულად ემპათიური იყოს და ნაკლებად გაამახვილოს ყურადღება მიზნებზე.','შესაძლოა გაუჭირდეს მკაცრი, მაგრამ საჭირო გადაწყვეტილებების მიღება.','შეიძლება ნაკლებად წარმოაჩინოს საკუთარი თავი, რაც ქმნის შთაბეჭდილებას, რომ მისი როლი მეორეხარისხოვანია.']),
('PEVE', 'კულტურის ელჩი', 'კულტურის ელჩი', 'People (ადამიანები)', 'Execution (აღსრულება)', 'Vision (ხედვა)', 'კულტურის ელჩი არის ლიდერი, რომელიც ღიად იცავს და ავითარებს ორგანიზაციის ღირებულებებს. მისთვის ბუნებრივია, იყოს მისაბაძი მაგალითი და სხვებს შთააგონოს ერთიანობისა და თანამშრომლობის სურვილი. იგი სწრაფად მოქმედებს, მკაფიოდ გამოხატავს საკუთარ პოზიციას და მოტივაციას მატებს გუნდს, თუმცა ხანდახან შესაძლოა ნაკლებად იფიქროს გრძელვადიან სტრატეგიაზე და ზედმეტად კონცენტრირდეს ყოველდღიურ პრაქტიკულ საკითხებზე.', ARRAY['ღირებულებების შესახებ ღია კომუნიკაცია.','ორგანიზაციის კულტურის პოპულარიზაცია და გაძლიერება.','გუნდში ერთიანობისა და მოტივაციის შექმნა.','სწრაფი გადაწყვეტილებების მიღება და მოქმედება.','პოზიტიური ატმოსფეროს შექმნა და თანამშრომლობის ხელშეწყობა.'], ARRAY['სტრატეგიული ხედვის გაძლიერება – მეტი დროის დათმობა გრძელვადიანი სტრატეგიისთვის.','პრიორიტეტების გამოკვეთა – ოპერაციულ აქტივობებს სჭირდება სტრუქტურა და კოორდინაცია.','ინოვაციის მიღება – ცვლილებების მიღება, რომლებიც გააუმჯობესებს კულტურას.'], ARRAY['შესაძლოა ზედმეტად ფოკუსირდეს ყოველდღიურ დეტალებზე და ყურადღება არ მიაქციოს ფართო სურათს.','შეიძლება პრაგმატული შედეგები შეაფასოს ნაკლებად, ვიდრე კულტურული სიმბოლიკა.','შეიძლება იყოს დამოკიდებული საკუთარი ხილულობისა და ქარიზმის ეფექტზე.']),
('PEVI', 'კულტურის მცველი', 'კულტურის მცველი', 'People (ადამიანები)', 'Execution (აღსრულება)', 'Vision (ხედვა)', 'კულტურის მცველი იცავს და ავითარებს ორგანიზაციის ღირებულებებს სიღრმისეულად და საფუძვლიანად. ის არაა ხმაურიანი ლიდერი, თუმცა მისი ქცევები და თანმიმდევრული დამოკიდებულება ქმნის მხარდაჭერის გარემოსა და ნდობას. მისთვის მნიშვნელოვანი არის სტაბილურობა, წესრიგი და ისეთი გარემოს ჩამოყალიბება, სადაც ადამიანებს კომფორტულად შეუძლიათ მუშაობა, თუმცა ხანდახან შეიძლება გაუჭირდეს ინოვაციის ან სწრაფი ცვლილებების მიღება.', ARRAY['ღირებულებებზე დაფუძნებული და თანმიმდევრული ქმედებები.','მხარდაჭერის გარემოსა და ნდობის შექმნა გუნდში.','პროცესების სტაბილურობა და ოპერაციული დისციპლინა.','რირებულებების დაცვა და მისი ყოველდღიურობაში ინტეგრაცია.','სტრუქტურისა და წესრიგის შენარჩუნება.'], ARRAY['ინოვაციის მიმღებლობა – სიახლეების უფრო აქტიურად მიღება.','გრძელვადიანი ხედვის განვითარება – ყოველდღიური სტაბილურობის პარალელურად, სტრატეგიულ მიმართულებაზეც ფოკუსირება.','კომუნიკაციის გაძლიერება – კულტურული ღირებულებების უფრო აქტიურად, ღიად გაზიარება სხვებთან.'], ARRAY['შეიძლება ზედმეტად კონსერვატიული იყოს და ვერ მიიღოს ცვლილებები.','შესაძლოა უარყოს ინიციატივა, თუ ახალი იდეა არღვევს სტაბილურობას.','ნაკლები ხილულობა შეიძლება აღქმული იქნას როგორც ჩაკეტილობა ან ინერტულობა.']),
('EVPE', 'ხედვის იმპლემენტატორი', 'ხედვის იმპლემენტატორი', 'Execution (აღსრულება)', 'Vision (ხედვა)', 'People (ადამიანები)', 'ხედვის იმპლემენტატორი არის ქარიზმატული ლიდერი, რომელიც მომავლის ფართო ხედვას აქცევს კონკრეტულ შედეგად და ამას აკეთებს ენერგიულად და საჯაროდ. მისთვის ბუნებრივია იყოს შედეგებზე ორიენტირებული და მკაფიოდ განსაზღვროს მიმართულება. მას შეუძლია რთული სტრატეგიების გარდაქმნა პრაქტიკულ ნაბიჯებად და ამ პროცესში სხვების შთაგონებაც, თუმცა ზოგჯერ შეიძლება ნაკლებად გაამახვილოს ყურადღება ადამიანურ ურთიერთობებზე და ემოციურ ფაქტორებზე.', ARRAY['ხედვის ეფექტურად გარდაქმნა ქმედებად და შედეგად.','მაღალი მოტივაცია და ენერგიულობა, რომელიც გუნდს აძლევს იმპულსს.','სწრაფი გადაწყვეტილებების მიღება და შედეგებზე ორიენტაცია.','გუნდის მობილიზაცია საჯარო კომუნიკაციითა და ქარიზმით.','გრძელვადიანი მიზნების ქმედებით მხარდაჭერა.'], ARRAY['ემპათიის გაძლიერება – ადამიანებთან ღრმა კავშირების დამყარება','გუნდური პროცესების გათვალისწინება – შედეგებთან ერთად ყურადღება კულტურასა და ურთიერთობებზე.','ბალანსი სისწრაფესა და ანალიზს შორის – სწრაფ ქმედებებს სჭირდება ღრმა ანალიზიც.'], ARRAY['შესაძლოა ნაკლებად გაითვალისწინოს გუნდის ემოციური მდგომარეობა.','შეიძლება ზედმეტად შედეგებზე იყოს ორიენტირებული და არ მიაქციოს ყურადღება ადამიანურ ურთიერთობებს.','შეიძლება სწრაფი ქმედებების სასარგებლოდ სტრატეგიული ანალიზი უგულებელყოს.']),
('EVPI', 'სისტემების დიზაინერი', 'სისტემების დიზაინერი', 'Execution (აღსრულება)', 'Vision (ხედვა)', 'People (ადამიანები)', 'სისტემების დიზაინერი ანალიზზე ორიენტირებული მიდგომით ქმნის სტაბილურ და საიმედო პროცესებს, რომლებიც დიდ ხედვებს გარდაქმნის მდგრად შედეგებად. მისთვის მნიშვნელოვანია სისტემურობა, სიზუსტე და წესრიგი. ის ნაკლებად ჩანს სცენაზე, მაგრამ მისი მუშაობა ქმნის მყარ საფუძველს ორგანიზაციის განვითარებისთვის. მისი სიძლიერე არის სტაბილურობისა და სტრუქტურის შექმნა, თუმცა ხანდახან შეიძლება ნაკლებად გამოჩნდეს როგორც შთამაგონებელი ლიდერი.', ARRAY['სისტემების ჩამოყალიბება და სტაბილური პროცესების დიზაინი.','გრძელვადიანი ხედვის სტრუქტურირებით მხარდაჭერა.','სიზუსტე და პასუხისმგებლობა შედეგებზე.','მდგრადობის უზრუნველყოფა გუნდისთვის.','სტრატეგიის პრაქტიკულ რეალობად ქცევა.'], ARRAY['ხილვადობის გაზრდა – საკუთარი წვლილის უფრო თვალსაჩინოდ გამოხატვა.','ადამიანურ ურთიერთობებზე მეტი აქცენტი – ემოციური ინტელექტის გამოყენება ნდობის გასაძლიერებლად.','მოქნილობა – ცვლილებებისადმი სწრაფი ადაპტაცია სტაბილურობის შენარჩუნების პარალელურად.'], ARRAY['შეიძლება მისი წალისხმევა ნაკლებად წარმოაჩინოს, რის გამოც მისი როლი შეუმჩნეველი დარჩეს.','შესაძლოა ზედმეტად დამოკიდებული იყოს წესრიგსა და სტრუქტურაზე და გაუჭირდეს მოქნილობა.','შეიძლება ნაკლებად შთააგონოს სხვები, რადგან მის სტილს ნაკლებად ახასიათებს ქარიზმა.']),
